import { Link } from "react-router-dom";
import styles from "./styles.module.css";

const Main = () => {
	const handleLogout = () => {
		localStorage.removeItem("token");
		window.location.reload();
	};
	function openPopup() {
		window.close();
		const popup = window.open("http://localhost:5501/Gameplay/devApp.html", "Popup", `width=1500,height=1400`);
		if (popup) {
		  popup.opener = null;
		}
	  }

	return (
		<div className={styles.main_container}>
			<nav className={styles.navbar}>
				<h1>Welcome to Black Jack</h1>
				<button className={styles.white_btn} onClick={handleLogout}>
					Logout
				</button>
			</nav>
			<main>
			<button onClick={openPopup}>Let's get started !</button>

			</main>
		</div>
	);
};

export default Main;
