import styles from "./styles.module.css";
import { useState, useEffect } from "react";
import axios from "axios";

const Main = () => {
  const handleLogout = () => {
    localStorage.removeItem("token");
    window.location.reload();
  };

  // This function closes the login page and
  // opens up the window for gameplay
  function openGame() {
    window.close();
    const popup = window.open(
      "http://localhost:5501/Gameplay/devApp.html",
      "Popup",
      `width=2000,height=2000`
    );
    if (popup) {
      popup.opener = null;
    }
  }

  const [user, setUser] = useState(null);
  const [usersList, setUsersList] = useState([]);

  // Fetch user data from the server on component mount
  useEffect(() => {
	const token = localStorage.getItem("token");
	axios
	  .get("/api/profile", {
		headers: { Authorization: `Bearer ${token}` },
	  })
	  .then((response) => {
		setUser(response.data[0]); // Assuming the profile API returns a single user object
		setUsersList(response.data);
		console.log(usersList); // Logging the usersList state after setting it
	  })
	  .catch((error) => console.log(error));
  }, [usersList]);



  return (
    <div className={styles.main_container}>
      <nav className={styles.navbar}>
        <h1>Welcome to Black Jack</h1>
      </nav>
      <div className={styles.account_info}>
        <h2>Account Info</h2>
        {user && (
          <div>
            User: {`${user.firstName} ${user.lastName}`}
            <br />
            Money: {user.money}
          </div>
        )}
      </div>
      <table className={styles.table}>
			<tr>
			<th scope="col">ID</th>
			<th scope="col">First Name</th>
			<th scope="col">Last Name</th>
			<th scope="col">Email</th>
			<th scope="col">Money</th>
			</tr>
        <tbody>
		{usersList.map((user) => {
			return (
			<tr key={user._id}>
				<th scope="row">{user._id}</th>
				<td>{user.firstName}</td>
				<td>{user.lastName}</td>
				<td>{user.email}</td>
				<td>{user.money}</td>
			  </tr>
			)
		})}
        </tbody>
      </table>
      <p>
        <button onClick={openGame}>Let's get started!</button>
      </p>
      <button className={styles.white_btn} onClick={handleLogout}>
        Logout
      </button>
    </div>
  );
};

export default Main;
