import { Link } from "react-router-dom";
import styles from "./styles.module.css";
import { useState } from "react";
import axios from "axios";

const Main = () => {
	const handleLogout = () => {
		localStorage.removeItem("token");
		window.location.reload();
	};

	// This function closes the login page and
	// open up the window for gameplay
	function openGame() {
		window.close();
		const popup = window.open("http://localhost:5501/Gameplay/devApp.html", "Popup", `width=2000,height=2000`);
		if (popup) {
		  popup.opener = null;
		}
	}

	const handleSubmit = async (e) => {
		e.preventDefault();
		try {
			const url = "http://localhost:8080/api/auth";
			const { data: res } = await axios.post(url, data);
			localStorage.setItem("token", res.data);
			window.location = "/";
		} catch (error) {
			if (
				error.response &&
				error.response.status >= 400 &&
				error.response.status <= 500
			) {
				setError(error.response.data.message);
			}
		}
	};

	return (
		<div className={styles.main_container}>
			<nav className={styles.navbar}>
				<h1>Welcome to Black Jack</h1>
			</nav>
			<div id="account-info"> 
				Account Info
				<div>{data.money}</div> 
			</div>
			<p>
				<button onClick={openGame}>Let's get started !</button>
			</p>
			<button className={styles.white_btn} onClick={handleLogout}>
				Logout
			</button>
		</div>
	);
};

export default Main;
